create table doadores (cpf varchar(255) not null, altura double precision, bairro varchar(255), celular varchar(255), cep varchar(255), cidade varchar(255), data_nasc datetime(6), email varchar(255), endereco varchar(255), estado varchar(255), mae varchar(255), nome varchar(255), numero integer not null, pai varchar(255), peso double precision, rg varchar(255), sexo varchar(255), telefone_fixo varchar(255), tipo_sanguineo varchar(255), primary key (cpf)) engine=InnoDB;
